import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const url = "http://localhost:8000/api";

const listado = () => {
  return;
};

const Index = () => {
  const [ingresos, setIngresos] = useState([]);

  useEffect(() => {
    listarIngresos();
  }, []);

  const listarIngresos = async () => {
    const response = await axios.get(`${url}/ingresos`);
    // console.log(response.data.ingresos);
    setIngresos(response.data.ingresos);
  };

  const eliminarIngreso = async (id) => {
    await axios.delete(`${url}/ingreso/${id}`);
    listarIngresos();
  };
  return (
    <div>
      <div className="d-grid gap-2">
        <Link
          to="/create"
          className="btn btn-success btn-sm mt-2 mb-2 text-white"
        >
          Agregar +
        </Link>
      </div>
    </div>
  );
};

export default Index;
