import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const url = "http://localhost:8000/api";

const Index = () => {
  const [ingresos, setIngresos] = useState([]);

  useEffect(() => {
    listarIngresos();
  }, []);

  const listarIngresos = async () => {
    const response = await axios.get(`${url}/egresos`);
    setIngresos(response.data.egresos);
  };

  const listado = () => {
    return (
      <table className="table table-striped">
        <thead className="bg-primary text-white">
          <tr>
            <th>Descripcion</th>
            <th>Monto</th>
            <th>Fecha</th>
            <th>Opciones</th>
          </tr>
        </thead>
        <tbody>
          {ingresos.map((obj) => (
            <tr key={obj.id}>
              <td> {obj.descripcion} </td>
              <td> {obj.monto} </td>
              <td> {obj.fecha} </td>
              <td>
                <Link to={`/egreso/edit/${obj.id}`} className="btn btn-info">
                  Editar
                </Link>
                |
                <button
                  onClick={() => eliminarIngreso(obj.id)}
                  className="btn btn-danger"
                >
                  Eliminar
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    );
  };
  const eliminarIngreso = async (id) => {
    let url_delete = `${url}/egreso/${id}`;
    console.log(url_delete);
    await axios.delete(`${url}/egreso/${id}`);
    listarIngresos();
  };
  return (
    <div>
      <div className="d-grid gap-2">
        <Link
          to="/egreso/create"
          className="btn btn-success btn-sm mt-2 mb-2 text-white"
        >
          Agregar +
        </Link>
      </div>
      {listado()}
    </div>
  );
};

export default Index;
