import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const url = "http://localhost:8000/api";

const Index = () => {
  const [mes, setMes] = useState(10);
  const [ano, setAno] = useState(2022);

  const [ganancias, setGanancias] = useState([]);
  const [ingresos, setIngresos] = useState([]);
  const [egresos, setEgresos] = useState([]);

  useEffect(() => {
    listarIngresos();
  }, [mes, ano]);

  const listarIngresos = async () => {
    const response = await axios.get(`${url}/ganancias/${mes}/${ano}`);
    setGanancias(response.data.ganancias);
    setIngresos(response.data.ingresos);
    setEgresos(response.data.egresos);
  };

  const handleChange = (e) => {
    const seleccion = e.target.value.split("-");
    setMes(parseInt(seleccion[1]));
    setAno(parseInt(seleccion[0]));
    listarIngresos();
  };

  const listado = () => {
    return (
      <table className="table table-striped">
        <thead className="bg-primary text-white">
          <tr>
            <th>Tranccion</th>
            <th>Monto</th>
            <th>Opciones</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>Ganancias</td>
            <td> {ganancias} </td>
            <td></td>
          </tr>
          <tr>
            <td>Ingresos</td>
            <td> {ingresos} </td>
            <td>
              <Link to={`/ingresos`} className="btn btn-sm btn-info">
                Detalle
              </Link>
            </td>
          </tr>
          <tr>
            <td>Egresos</td>
            <td> {egresos} </td>
            <td>
              <Link to={`/ingresos`} className="btn btn-sm btn-info">
                Detalle
              </Link>
            </td>
          </tr>
        </tbody>
      </table>
    );
  };

  return (
    <div>
      <div className="d-grid gap-2">
        <p>
          <h1>Ganancias Mensuales</h1>
          <input
            onChange={(e) => handleChange(e)}
            type="month"
            className="form-control"
          />
        </p>
        {listado()}
      </div>
    </div>
  );
};

export default Index;
