import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const url = "http://localhost:8000/api";

const Index = () => {
  const [ganancias, setGanancias] = useState([]);
  const [ingresos, setIngresos] = useState([]);
  const [egresos, setEgresos] = useState([]);

  useEffect(() => {
    listarIngresos();
  }, []);

  const listarIngresos = async () => {
    let mes = 1;
    let ano = 2022;
    const response = await axios.get(`${url}/ganancias/${mes}/${ano}`);
    console.log(response.data);
    setGanancias(response.data.ganancias);
    setIngresos(response.data.ingresos);
    setEgresos(response.data.egresos);
  };

  const listado = () => {
    return (
      <table className="table table-striped">
        <thead className="bg-primary text-white">
          <tr>
            <th>Tranccion</th>
            <th>Monto</th>
            <th>Opciones</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>Ganancias</td>
            <td> {ganancias} </td>
            <td></td>
          </tr>
          <tr>
            <td>Ingresos</td>
            <td> {ingresos} </td>
            <td>
              <Link to={`/ingresos`} className="btn btn-sm btn-info">
                Ver
              </Link>
            </td>
          </tr>
          <tr>
            <td>Egresos</td>
            <td> {egresos} </td>
            <td>
              <Link to={`/ingresos`} className="btn btn-sm btn-info">
                Ver
              </Link>
            </td>
          </tr>
        </tbody>
      </table>
    );
  };

  return (
    <div>
      <div className="d-grid gap-2">{listado()}</div>
    </div>
  );
};

export default Index;
